INSERT INTO `oc_product_description` (`product_id`, `language_id`, `name`, `description`, `tag`, `meta_title`, `meta_description`, `meta_keyword`) VALUES
(76, 2, 'Наполнитель для кошачьего туалета TOP CAT Mini бентонитовый с ароматом лаванды 0.8-1.5 мм 5+1 кг ', '&lt;p&gt;ТОР САТ MINI гигиенический наполнитель с ароматом лаванды 6 кг&lt;/p&gt;&lt;p&gt;Глиняный наполнитель ТОР САТ для кошачьего туалета изготовлен на основе бентонита высшего качества.&lt;/p&gt;&lt;p&gt;&lt;br&gt;&lt;/p&gt;&lt;p&gt;Состоит из гранул большого размера, что значительно улучшает способность наполнителя к впитыванию влаги и поглощению запахов, в больших гранулах особенно много пор, поэтому они впитывают жидкость подобно губке и хорошо поглощают запахи.&lt;/p&gt;&lt;p&gt;&lt;br&gt;&lt;/p&gt;&lt;p&gt;Бентонит обладает высокими катионообменными и адсорбционными свойствами, являясь сильнейшим природным сорбентом, интенсивно поглощает запах и обладает антибактериальным действием.&lt;/p&gt;&lt;p&gt;При производстве наполнителя на основе бентонита могут использоваться ароматические добавки и средства для дополнительной борьбы с запахами и бактериями. В данном случае добавки усиливают природные свойства бентонита.&lt;/p&gt;&lt;p&gt;В общем, на мировом рынке бентонитовые наполнители считаются оптимальным решением для туалета кошек.&lt;/p&gt;&lt;p&gt;Почему TOP CAT MINI?&lt;/p&gt;&lt;p&gt;&lt;br&gt;&lt;/p&gt;&lt;p&gt;Маленькие гранулы: Идеально подходят для кошек с короткой шерстью, не липнут к кавычкам.&lt;/p&gt;&lt;p&gt;Комкуется: Легко убирать, не оставляет грязи и запаха.&lt;/p&gt;&lt;p&gt;Аромат лаванды: Нейтрализует неприятные запахи, даря свежесть и чистоту.&lt;/p&gt;&lt;p&gt;Экономный: Затрачивается на 30% меньше, чем другие наполнители.&lt;/p&gt;&lt;p&gt;Безопасный: Изготовлен из экологически чистой глины, не содержит вредных примесей.&lt;/p&gt;&lt;p&gt;Преимущества:&lt;/p&gt;&lt;p&gt;&lt;br&gt;&lt;/p&gt;&lt;p&gt;Масса одного пакета 6 кг, позволяющая использовать его более длительный период времени.&lt;/p&gt;&lt;p&gt;Не вызывает аллергических реакций.&lt;/p&gt;&lt;p&gt;Удобная бумажная упаковка, отвечающая всем требованиям по охране окружающей среды.&lt;/p&gt;&lt;p&gt;Надежно нейтрализует и удерживает запах.&lt;/p&gt;&lt;p&gt;Привлекательное соотношение цена-качество.&lt;/p&gt;&lt;p&gt;Высокое поглощение, быстрое и эффективное образование крепких комочков.&lt;/p&gt;&lt;p&gt;Комкуется, не образуя пыли.&lt;/p&gt;&lt;p&gt;Состав: 100% натуральный продукт – бентонит – природный минерал, разновидность глины. С ароматом лаванды.&lt;/p&gt;&lt;p&gt;Способ применения комкующихся наполнителей, ТОР САТ:&lt;/p&gt;&lt;p&gt;&lt;br&gt;&lt;/p&gt;&lt;p&gt;в сухой туалет насыпать слой высотой 5-7 см;&lt;/p&gt;&lt;p&gt;после каждого использования убирайте образовавшиеся комки (с помощью лопатки для песка);&lt;/p&gt;&lt;p&gt;добавьте свежий наполнитель, чтобы уровень всегда был 5-7 см;&lt;/p&gt;&lt;p&gt;обязательно 1 раз в месяц производите полную замену наполнителя.&lt;/p&gt;&lt;p&gt;&lt;br&gt;&lt;/p&gt;&lt;p&gt;Срок хранения:&lt;/p&gt;&lt;p&gt;В оригинальной упаковке неограничен. Неиспользуемую часть наполнителя следует хранить в закрытой, сухой упаковке.&lt;/p&gt;', '', 'Наполнитель для кошачьего туалета TOP CAT Mini бентонитовый с ароматом лаванды 0.8-1.5 мм 5+1 кг ', 'Наполнитель для кошачьего туалета TOP CAT Mini (5+1 кг) — бентонитовый наполнитель с ароматом лаванды, размер гранул 0.8-1.5 мм. Эффективно впитывает влагу и нейтрализует запахи, обеспечивая чистоту и комфорт для вашего питомца.', ''),
(77, 3, 'Наповнювач для котячого туалету Top Cat Ultra New Clumping Formula бентонітовий 0.5-3 мм 5 л', '&lt;p&gt;Наповнювач для котячого туалету ТОР САТ ULTRA бентонітовий new clumping formula 0,5-3 мм 5 л - це наповнювач, що комкується. Він має наступні переваги:&lt;/p&gt;&lt;p&gt;Добре вбирає вологу та запахи;&lt;/p&gt;&lt;p&gt;Утворює щільні грудки, які легко видаляються з лотка;&lt;/p&gt;&lt;p&gt;Не прилипає до лап та вовни кішок;&lt;/p&gt;&lt;p&gt;Чи не порошить;&lt;/p&gt;&lt;p&gt;Не містить шкідливих речовин.&lt;/p&gt;&lt;p&gt;Наповнювач ТОР САТ ULTRA підходить для всіх порід котів та кошенят. Він має велику фракцію (0,5-3 мм), що забезпечує хорошу поглинання вологи та запахів. Наповнювач не припадає пилом, що знижує ризик розвитку алергії у кішок та їх власників.&lt;/p&gt;&lt;p&gt;&lt;br&gt;&lt;/p&gt;&lt;p&gt;Ось деякі особливості наповнювача ТОР САТ ULTRA:&lt;/p&gt;&lt;p&gt;&lt;br&gt;&lt;/p&gt;&lt;p&gt;Наповнювач, що комкується&lt;/p&gt;&lt;p&gt;Виготовлений із бентоніту&lt;/p&gt;&lt;p&gt;Добре вбирає вологу та запахи&lt;/p&gt;&lt;p&gt;Утворює щільні грудки&lt;/p&gt;&lt;p&gt;Не прилипає до лап та вовни кішок&lt;/p&gt;&lt;p&gt;Не порошить&lt;/p&gt;&lt;p&gt;Не містить шкідливих речовин&lt;/p&gt;', '', 'Наповнювач для котячого туалету Top Cat Ultra New Clumping Formula бентонітовий 0.5-3 мм 5 л', 'Наповнювач для котячого туалету Top Cat Ultra (5 л) з новою формулою, що комкується. Бентонітовий наповнювач з розміром гранул 0.5-3 мм ефективно вбирає вологу та нейтралізує запахи, забезпечуючи чистоту та комфорт для вашого хвостатого.', ''),
(77, 2, 'Наполнитель для кошачьего туалета Top Cat Ultra new clumping formula бентонитовый 0.5-3 мм 5 л', '&lt;p&gt;Наполнитель для кошачьего туалета ТОР САТ ULTRA бентонитовый new clumping formula 0,5-3 мм 5 л - это комкующийся наполнитель, изготовленный из бентонита. Он обладает следующими преимуществами:&lt;/p&gt;&lt;p&gt;Хорошо впитывает влагу и запахи;&lt;/p&gt;&lt;p&gt;Образует плотные комки, которые легко удаляются из лотка;&lt;/p&gt;&lt;p&gt;Не прилипает к лапам и шерсти кошек;&lt;/p&gt;&lt;p&gt;Не пылит;&lt;/p&gt;&lt;p&gt;Не содержит вредных веществ.&lt;/p&gt;&lt;p&gt;Наполнитель ТОР САТ ULTRA подходит для всех пород кошек и котят. Он имеет крупную фракцию (0,5-3 мм), что обеспечивает хорошую впитываемость влаги и запахов. Наполнитель не пылит, что снижает риск развития аллергии у кошек и их владельцев.&lt;/p&gt;&lt;p&gt;&lt;br&gt;&lt;/p&gt;&lt;p&gt;Вот некоторые особенности наполнителя ТОР САТ ULTRA:&lt;/p&gt;&lt;p&gt;&lt;br&gt;&lt;/p&gt;&lt;p&gt;Комкующийся наполнитель&lt;/p&gt;&lt;p&gt;Изготовлен из бентонита&lt;/p&gt;&lt;p&gt;Хорошо впитывает влагу и запахи&lt;/p&gt;&lt;p&gt;Образует плотные комки&lt;/p&gt;&lt;p&gt;Не прилипает к лапам и шерсти кошек&lt;/p&gt;&lt;p&gt;Не пылит&lt;/p&gt;&lt;p&gt;Не содержит вредных веществ&lt;/p&gt;', '', 'Наполнитель для кошачьего туалета Top Cat Ultra new clumping formula бентонитовый 0.5-3 мм 5 л', 'Наполнитель для кошачьего туалета Top Cat Ultra (5 л) с новой комкующейся формулой. Бентонитовый наполнитель с размером гранул 0.5-3 мм эффективно впитывает влагу и нейтрализует запахи, обеспечивая чистоту и комфорт для вашего питомца.', ''),
(78, 3, 'Дерев\'яний наповнювач TOP CAT з ароматом полуниці, 3кг', '&lt;p&gt;&lt;span style=&quot;outline: none; color: rgb(39, 35, 35); font-family: Montserrat, sans-serif; font-size: 15px;&quot;&gt;Деревний наповнювач&lt;/span&gt;&lt;span style=&quot;color: rgb(39, 35, 35); font-family: Montserrat, sans-serif; font-size: 15px;&quot;&gt;&amp;nbsp;виготовляється з пресованих у гранули розміром 3-5 мм тирси хвойних порід дерев - ялинки або сосни. Завдяки 100% натуральному складу наповнювач є безпечним, не викликає алергії у котів та їх власників, має антибактеріальні властивості, може використовуватися для кошенят. Єдиний недолік - підвищене утворення пилу та розсипання продукту на дрібні шматочки у процесі використання. Після контакту з рідиною гранули перетворюються на тирсу, тому такий наповнювач необхідно частіше міняти, ніж силікагелевий і мінеральний матеріал.&lt;/span&gt;&lt;/p&gt;', '', 'Дерев\'яний наповнювач TOP CAT з ароматом полуниці, 3кг', 'Дерев\'яний наповнювач TOP CAT з ароматом полуниці (3 кг) забезпечує відмінне вбирання вологи та нейтралізує запахи. Натуральний склад та приємний фруктовий аромат створюють комфортну атмосферу для вашого вихованця.', ''),
(78, 2, 'Деревянный наполнитель TOP CAT с ароматом клубники, 3кг', '&lt;p&gt;Древесный наполнитель изготавливается из прессованных в гранулы размером 3-5 мм опилок хвойных пород деревьев – елки или сосны. Благодаря 100% натуральному составу наполнитель является безопасным, не вызывает аллергии у кошек и их владельцев, обладает антибактериальными свойствами, может использоваться для котят. Единственный недостаток – повышенное образование пыли и рассыпание продукта на мелкие кусочки в процессе использования. После контакта с жидкостью гранулы превращаются в опилки, поэтому такой наполнитель необходимо чаще менять, чем силикагелевый и минеральный материал.&lt;/p&gt;', '', 'Деревянный наполнитель TOP CAT с ароматом клубники, 3кг', 'Деревянный наполнитель TOP CAT с ароматом клубники (3 кг) обеспечивает отличное впитывание влаги и нейтрализует запахи. Натуральный состав и приятный фруктовый аромат создают комфортную атмосферу для вашего питомца.', ''),
(60, 3, 'Наповнювач туалету для кішок Top Cat 3,8 L (силікагелевий)', '&lt;p&gt;ТОР САТ - силікагелевий наповнювач для туалету, має чудові сорбуючі властивості. Рідина та запах поглинаються силікагелем практично миттєво, а сам наповнювач зберігає жорсткість фракції та не розноситься на лапках вихованця по квартирі. Рідкі відходи залишаються ув\'язненими всередині гранул наповнювача. ТОР САТ має гарний бактерицидний ефект. При поглинанні силікагель поступово змінює колір. Поступове наповнення гранул рідиною дозволяє легко контролювати рівень використання наповнювача. Оптимальний розмір фракції сприяє швидкому звиканню вихованця до цього наповнювача. Силікагелевий наповнювач ТОР САТ екологічно безпечний - при попаданні в ґрунт його кристали сприяють меліорації. ПЕРЕВАГИ: - має високу поглинаючу здатність і може повністю усунути запах на тривалий проміжок часу; - силікагелевий сорбент дуже економічний і тому досить недорогий у використанні; - дуже легкий, особливо в порівнянні з бентонітовими та дерев\'яними яєчними наповнювачами; — вартість вища, ніж природних, але поглинаюча здатність набагато вища, а отже, економічність набагато більша. Метод використання: - Наповніть сухий та чистий туалет кулею &quot;Top Cat&quot; від 3 до 5 см; - регулярно прибирайте тверді відходи; - перемішуйте вміст лотка раз на 1-2 дні; - один раз на 4 тижні повністю замінюйте наповнювач на свіжий; — Не утилізуйте його в унітаз, щоб уникнути забруднення каналізаційних стоків. Якщо Вашому вихованцю важко звикнути до силікагелевого наповнювача, спробуйте протягом тижня змішувати Top Cat з наповнювачем, яким користувалися раніше, у співвідношенні 1:1. Після наповнення кристалів рідиною здійсніть повну заміну наповнювача.&lt;/p&gt;', '', 'Наповнювач туалету для кішок Top Cat  3,8 L (силікагелевий)', 'Наповнювач Топ Кет із соєвих волокон з ароматом троянди - ідеальний вибір для дбайливих власників кішок. Забезпечує відмінну поглинання, нейтралізує запахи і дарує ніжний аромат троянд. Екологічний та безпечний для ваших вихованців.', ''),
(60, 2, 'Наполнитель туалета для кошек Top Cat 3,8L (силикагелевый)', '&lt;p&gt;ТОР САТ - силикагелевый наполнитель для туалета, обладает превосходными сорбирующими свойствами. Жидкость и запах поглощаются силикагелем практически мгновенно, а сам наполнитель сохраняет жесткость фракции и не разносится на кавычках питомца по квартире. Жидкие отходы остаются заключенными внутри гранул наполнителя.&amp;nbsp; &amp;nbsp;ТОР САТ обладает хорошим бактерицидным эффектом. При впитывании силикагель постепенно меняет цвет. Постепенное наполнение гранул жидкостью позволяет легко контролировать степень использования наполнителя. Оптимальный размер фракции способствует быстрому привыканию питомца к данному наполнителю. Силикагелевый наполнитель ТОР САТ экологически безопасен - при попадании в почву его кристаллы способствуют мелиорации.&amp;nbsp; &amp;nbsp; ПРЕИМУЩЕСТВА:&amp;nbsp; &amp;nbsp; — обладает высокой поглощающей способностью и может полностью устранить запах на длительный промежуток времени; — силикагелевой сорбент очень экономичен и потому достаточно недорог в использовании; — очень легкий, особенно по сравнению с бентонитовыми и деревянными яичными наполнителями; — стоимость выше, чем природных, но впитывающая способность намного выше, а значит, экономичность гораздо больше.&amp;nbsp; &amp;nbsp; Метод использования:&amp;nbsp; &amp;nbsp; - Наполните сухой и чистый туалет шаром &quot;Top Cat&quot; от 3 до 5 см; - регулярно убирайте твердые отходы; - перемешивайте содержимое лотка раз в 1-2 дня; - один раз в 4 недели полностью заменяйте наполнитель на свежий; — Не утилизируйте его в унитаз во избежание загрязнения канализационных стоков.&amp;nbsp; &amp;nbsp;В случае если Вашему питомцу трудно привыкнуть к силикагелевому наполнителю, попробуйте в течение недели смешивать &quot;Top Cat&quot; с наполнителем, которым пользовались ранее, в соотношении 1:1. После наполнения кристаллов жидкостью произведите полную замену наполнителя.&lt;/p&gt;', '', 'Наполнитель туалета для кошек Top Cat  3,8L (силикагелевый)', 'Наполнитель Топ Кэт из соевых волокон с ароматом розы - идеальный выбор для заботливых владельцев кошек. Обеспечивает отличную впитываемость, нейтрализует запахи и дарит нежный аромат роз. Экологичный и безопасный для ваших питомцев.', ''),
(61, 3, 'Наповнювач для туалетів бентонітовий 5+1 кг Top Cat mini (0,8-1,5 мм) лаванда', '&lt;p style=&quot;margin-bottom: 12px; font-variant-alternates: inherit; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-position: inherit; padding: 0px; border: 0px; font-stretch: inherit; line-height: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;&lt;font color=&quot;#333333&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;ТОР САТ MINI гігієнічний наповнювач дрібний з ароматом лаванди 6 кг&lt;/span&gt;&lt;/font&gt;&lt;/p&gt;&lt;p style=&quot;margin-bottom: 12px; font-variant-alternates: inherit; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-position: inherit; padding: 0px; border: 0px; font-stretch: inherit; line-height: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;&lt;font color=&quot;#333333&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;&lt;br&gt;&lt;/span&gt;&lt;/font&gt;&lt;/p&gt;&lt;p style=&quot;margin-bottom: 12px; font-variant-alternates: inherit; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-position: inherit; padding: 0px; border: 0px; font-stretch: inherit; line-height: inherit; font-optical-sizing: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit; vertical-align: baseline;&quot;&gt;&lt;font color=&quot;#333333&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;Глиняний наповнювач ТОР САТ, що комкується, для котячого туалету виготовлений на основі бентоніту вищої якості.&lt;/span&gt;&lt;/font&gt;&lt;/p&gt;&lt;p style=&quot;margin-bottom: 12px; font-variant-alternates: inherit; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-variant-position: inherit; p